  };

  render() {
    const { book } = this.props;

    return (
      <React.Fragment>
      <ul>
        <li>{book.id}</li>
        <li>{book.title}</li>
        <li>{book.author}</li>

        <button onClick={this.onClickSetFav(book.id)}>
          {this.state.fav ? (
            <i className="fas fa-heart" />
          ) : (
            <i className="far fa-heart" />
          )}
        </button>
        </ul>
      </React.Fragment>
    );
  }
}
export default Books;
