import React, {Component} from 'react'
import Login from './login'
import firebase from 'firebase'

var config = {
  apiKey: "AIzaSyDb6MxVPz02Dzu6TBU6uRMZk6lSfsEuH8E",
  authDomain: "solwit-pjatk-arc-2018-gr3.firebaseapp.com",
  databaseURL: "https://solwit-pjatk-arc-2018-gr3.firebaseio.com",
  projectId: "solwit-pjatk-arc-2018-gr3",
  storageBucket: "solwit-pjatk-arc-2018-gr3.appspot.com",
  messagingSenderId: "314895835823"

}
firebase.initializeApp(config)

const firestore = firebase.firestore()
const settings = {timestampsInSnapshots: true}
firestore.settings(settings)

class App extends Component {

  state = {
    isLoggedIn: false,
    elements: []
  }

  componentDidMount() {
    this.unregisterAuthObserver = firebase.auth().onAuthStateChanged(
     (user) => this.setState({isLoggedIn: !!user})
    )

    firebase.firestore().collection('Books').onSnapshot(querySpanshot => {
      const elements = []
      querySpanshot.forEach(doc => elements.push({id: doc.id, ...doc.data()})
      )
      this.setState({elements: elements})
    })
  }

  componentWillUnmount() {
    this.unregisterAuthObserver()
  }

  render() {
    const {isLoggedIn, elements} = this.state

    if(!isLoggedIn) {
      return (
        <div>
          <h1>Expedition</h1>
          <p>Please Log-in:</p>
          <Login/>
        </div>)
    } else {
      return (
        <div className='app'>
          <h1>Expedition</h1>
          <p>Welcome {firebase.auth().currentUser.displayName}! You are now loged-in!</p>
          <button onClick={() => firebase.auth().signOut()}>Logout</button>

          <p>Add additional books:</p>
          <form>
            <input
              type="text"
              name="title"
              placeholder="Title"
            />
            <input
              type="text"
              name="author"
              placeholder="Author"
            />
            <button type="submit">Submit</button>
          </form>

          <ul>
            {elements.map(el =>
              <li key={el.id}>
                {el.title} <i>{el.author}</i>
              </li>
            )}
          </ul>

        </div>
      )
    }
  }
}

export default App
