import React, {Component} from 'react'
import Login from './login'
import Add from './Adding'
import firebase from 'firebase'

var config = {
  apiKey: "AIzaSyDb6MxVPz02Dzu6TBU6uRMZk6lSfsEuH8E",
  authDomain: "solwit-pjatk-arc-2018-gr3.firebaseapp.com",
  databaseURL: "https://solwit-pjatk-arc-2018-gr3.firebaseio.com",
  projectId: "solwit-pjatk-arc-2018-gr3",
  storageBucket: "solwit-pjatk-arc-2018-gr3.appspot.com",
  messagingSenderId: "314895835823"

}
firebase.initializeApp(config)


class App extends Component {

  state = {
    isLoggedIn: false,
    elements: []
  }

  componentDidMount() {
    this.unregisterAuthObserver = firebase.auth().onAuthStateChanged(
     (user) => this.setState({isLoggedIn: !!user})
    )

//
  }

  componentWillUnmount() {
    this.unregisterAuthObserver()
  }

  constructor() {
    super();
    this.state = {
     title: '',
     author: ''
    };
}

updateInput = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
}

addBook = e => {
    e.preventDefault();
    const db = firebase.firestore();
    db.settings({
      timestampsInSnapshots: true
    });
    const userRef = db.collection('Books').add({
      title: this.state.title,
      author: this.state.author
    }); 
    this.setState({
      title: '',
      author: ''
    });
  };

  render() {
    const {isLoggedIn, elements} = this.state

    if(!isLoggedIn) {
      return (
        <div>
          <h1>Expedition</h1>
          <p>Please Log-in:</p>
          <Login/>
        </div>)
    } else {
      return (
        <div className='app'>
          <h1>Expedition</h1>
          <p>Welcome {firebase.auth().currentUser.displayName}! You are now loged-in!</p>
          <button onClick={() => firebase.auth().signOut()}>Logout</button>

          <p>Add additional books:</p>
          <form onSubmit={this.addBook}>
            <input
              type="text"
              name="title"
              placeholder="Title"
              onChange={this.updateInput}
              value={this.state.title}
            />
            <input
              type="text"
              name="author"
              placeholder="Author"
              onChange={this.updateInput}
              value={this.state.author}
            />
            <button type="submit">Submit</button>
          </form>
        </div>
      )
    }
  }
}

export default App
