import React, {Component} from 'react'


class Book extends Component {

    onClickSetFav = () => {
        this.props.onClickSetFavHandler();
        this.fav = false;
    }


    render() {

        return (
            <React.Fragment>
                {' '}
                <button onClick={this.setState(!this.fav)}>

                    {this.setFav = () => {return (this.fav) ? <i className="fas fa-heart"></i> : <i className="far fa-heart"></i>}}
                </button>
            </React.Fragment>
        )
    }
}
export default Book;
