import React, {Component} from 'react'
import firebase from 'firebase'
import Book from './Book'

class Books extends Component {

    state = {
        elements: [],
        isFaved: false
    };

    list(){
        firebase.firestore()
            .collection('Books')
            .onSnapshot(querySnapshot => {
                const elements = []
                querySnapshot.forEach(doc =>
                    elements.push({id: doc.id, ...doc.data()})
                )
                this.setState({elements: elements})
            })
    }

    setFav = id => {
        console.log(id)
    }

    render() {
        const {elements} = this.state
        this.list()

        return(
            <div>
                <p>List of known books: <ul>{elements.map(element => {return <li> {element} </li>})}</ul></p>
                <ul>
                    {elements.map(e =>
                        <li key={e.id}>
                            "{e.title}" &mdash; <i>{e.author}</i>
                            <Book onClickSetFavHandler = {this.setFav.bind(this, elements.id)} />
                        </li>
                    )}
                </ul>
            </div>
        )
    }
}
export default Books;
