import React, { Component } from 'react';
import firebase from 'firebase';
import Book from './Book';

class Books extends Component {
  state = {
    elements: [],
    isFaved: false
  };

  list() {
    firebase
      .firestore()
      .collection('Authors')
      .onSnapshot(querySnapshot => {
        const elements = [];
        querySnapshot.forEach(doc => elements.push({ id: doc.id }));
        this.setState({ elements });
      });
  }

  setFav = id => {
    console.log(id);
  };

  render() {
    const { elements } = this.state;
    this.list();

    return (
      <ul>
        {elements.map(e => (
          <Book
            key={e.id}
            book={e}
            onClickSetFavHandler={this.setFav.bind(this, e.id)}
          />
        ))}
      </ul>
    );
  }
}
export default Books;
