import React, { Component } from 'react'
import Books from './Books'
import firebase from 'firebase'

class Listing extends Component {

    state = {
        authors: [],
        books: []
    }

    componentDidMount() {
        var db = firebase.firestore()
            db.collection('Authors')
            .onSnapshot(querySnapshot => {
                const authors = []
                querySnapshot.forEach(doc =>
                    authors.push({ id: doc.id, ...doc.data() })
                )
                this.setState({ authors: authors })
            })

        this.state.authors.id.forEach(function(authorid) {
            db.collection('Authors').doc(authorid).collection('Books')
            .onSnapshot(querySnapshot => {
                const books = []
                querySnapshot.forEach(doc =>
                    books.push({ id: doc.id, authorid: authorid, author: this.state.author.name, ...doc.data() })
                )
            })
        })
    }

    render() {
        const { books } = this.state

        return (
            <div>
                <p>List of known books:</p>
                <ul>
                    {books.map(b =>
                        <Books key={b.id} book={b} />
                    )}
                </ul>
            </div>
        )
    }
}
export default Listing;
