import React, { Component } from 'react'
import Books from './Books'
import firebase from 'firebase'

class Listing extends Component {

    state = {
        authors: [],
        books: []
    }

    saveFirestore(doc, books) {
        books.push({
            id: doc.id,
            authorid: author.id,
            author: author.name,
            ...doc.data()
        })

        this.setState({ books: books })
    }

    componentDidMount() {
        var db = firebase.firestore()
        db.collection('Authors')
            .onSnapshot(querySnapshot => {
                const authors = []
                querySnapshot.forEach(doc =>
                    authors.push({ id: doc.id, ...doc.data() })
                )
                this.setState({ authors: authors })

                this.state.authors.forEach(author =>
                    db.collection('Authors').doc(author.id).collection('Books')
                        .onSnapshot(querySnapshot => {
                            querySnapshot.forEach(doc =>
                                this.saveFirestore(doc, books)
                                )
                        })
                )
            })
    }


    render() {
        const { books } = this.state

        return (
            <div>
                <p>List of known books:</p>
                <ul>
                    {books.map(b =>
                        <Books key={b.id} book={b} />
                    )}
                </ul>
            </div>
        )
    }
}
export default Listing;
