import React, { Component } from 'react';
import firebase from 'firebase';
import Books from './Books';
import Authors from './Authors';

class Listing extends Component {
  state = {
    authors: [],
    books: [],
    isFaved: false
  };

  list() {
    firebase
      .firestore()
      .collection('Authors')
      .onSnapshot(querySnapshot => {
        const authors = [];
        querySnapshot.forEach(doc => authors.push({ id: doc.id }));
        this.setState({ authors });
      });

    this.state.authors.map(author => {
      firebase
        .firestore()
        .collection(`Authors/${author.id}/Books`)
        .onSnapshot(querySnapshot => {
          const books = [];
          querySnapshot.forEach(doc =>
            books.push({ id: doc.id, ...doc.data() })
          );
          this.setState({ books });
        });
    });
  }

  setFav = id => {
    console.log(id);
  };

  render() {
    const { authors, books } = this.state;
    this.list();

    return (
      <React.fragment>
        <div>
          {books.map(book => (
            <Books
              key={book.id}
              book={book}
              onClickSetFavHandler={this.setFav.bind(this, book.id)}
            />
          ))}
        </div>

        <div>
          {authors.map(author => (
            <Authors key={author.id} author={author} />
          ))}
        </div>
      </React.fragment>
    );
  }
}
export default Listing;
