import React, { Component } from 'react';
import firebase from 'firebase';
import Book from './Book';

class Books extends Component {
  state = {
    authors: [],
    books: [],
    isFaved: false
  };

  list() {
    firebase
      .firestore()
      .collection('Authors')
      .onSnapshot(querySnapshot => {
        const authors = [];
        querySnapshot.forEach(doc => authors.push({ id: doc.id }));
        this.setState({ authors });
      });

    this.state.authors.map(e => {
      firebase
        .firestore()
        .collection(`Authors/${e.id}/Books`)
        .onSnapshot(querySnapshot => {
          const books = [];
          querySnapshot.forEach(doc =>
            books.push({ id: doc.id, ...doc.data() })
          );
          this.setState({ books });
        });
    });
  }

  setFav = id => {
    console.log(id);
  };

  render() {
    const { authors, books } = this.state;
    this.list();

    return (
      <ul>
        {books.map(e => (
          <Book
            key={e.id}
            book={e}
            onClickSetFavHandler={this.setFav.bind(this, e.id)}
          />
        ))}
      </ul>
    );
  }
}
export default Books;
