import React, { Component } from 'react';

class DisplayAddNotification extends Component {
  constructor(props) {
    super();

    this.state = { notification: '' };

    this.stopNotifications = this.stopNotifications.bind(this);

    this.notificationService = props.notificationService;
    this.subscription = this.notificationService.greetings().subscribe(res => {
      this.setState({ notification: res });
    });
  }

  render() {
    let notification = this.state.notification;
    return (
      <div>
        <div> {notification}</div>
        <button onClick={this.stopNotifications}>Stop</button>
      </div>
    );
  }

  stopNotifications() {
    this.subscription.unsubscribe();
  }
}

export default DisplayAddNotification;
